BYTE SD_ReadSingleBlock(UINT sector, BYTE *buffer)
{
  BYTE r1;
  WORD i;
  i=512;

   r1 = sd_sendcmd(17, sector, 1);        //å‘é€CMD17 è¯»å‘½ä»¤
   if(r1 != 0x00)        return r1;

   do{
                   r1 = wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
   }while(r1 != 0xFE);

   while(i!=0)
   {
           *buffer = wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
           buffer++;
           i--;
   }
   wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
   wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);


   return 0;                 //è¯»å–æ­£ç¡®ï¼Œè¿”å›0
}

BYTE SD_ReadMultiBlock(UINT sector, BYTE *buffer, BYTE count)
{
  BYTE r1;
  WORD i;

  r1 = sd_sendcmd(18, sector, 1);                //è¯»å¤šå—å‘½ä»¤
  if(r1 != 0x00)        return r1;

  while(count != 0){
          i = 512;
          do{
                        r1 = wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
          }while(r1 != 0xFE);

          while(i!=0)
          {
                   *buffer = wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
                   buffer++;
                   i--;
          }
          buffer+=512;
          count--;
          wbspi_master_txdata(PICO_WBSPI_MASTER,0xFF);
  }

  sd_sendcmd(12, 0, 1);        //å…¨éƒ¨ä¼ è¾“å®Œæˆï¼Œå‘é€åœæ­¢å‘½ä»¤
